information "Heat shock temperature sensitive cells."argument  cells_flask: sample , "cells to be heat shocked"endtake  cells = item cells_flaskendcells = cells[0]step  description: "Heat shock the cells"  note: " put them in a 50ml tube."  image: "aphoto"endstep  description: "Chill cells"  note: " ASAP!"  image: "aphoto"endproduce  hot_cells = 1 "Heat shocked tube" from cells  location: "Bench"  release cellsendlog  return: { cell_id: hot_cells[:id]}end